 allocp += n; 
 return allocp - n; /* old p */ 
 } else /* not enough room */ 
 return 0; 
 } 
 void afree(char *p) /* free storage pointed to by p */ 
 { 
 if (p >= allocbuf && p < allocbuf + ALLOCSIZE) 
 allocp = p; 
 }

