 main(int argc, char *argv[]) 
 { 
 char line[MAXLINE]; 
 long lineno = 0; 
 int c, except = 0, number = 0, found = 0; 
 while (--argc > 0 && (*++argv)[0] == '-') 
 while (c = *++argv[0]) 
 switch (c) { 
 case 'x': 
 except = 1; 
 break; 
 case 'n': 
 number = 1; 
 break; 
 default: 
