 argc = 0; 
 found = -1; 
 break; 
 } 
 if (argc != 1) 
 printf("Usage: find -x -n pattern\n"); 
 else 
 while (getline(line, MAXLINE) > 0) { 
 lineno++; 
 if ((strstr(line, *argv) != NULL) != except) { 
 if (number) 
